contracts/libraries/Random.sol.content:
// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.6;

/// @notice Formats, parses, and generates the DNA of our shadowy friends
/// @dev    Reads from the `block` in `getSeed`
library Random {
    uint256 private constant A = 0;
    uint256 private constant G = 1;
    uint256 private constant D = 2;
    uint256 private constant T = 3;

    /// @notice Builds a sequence from a seed
    function sequence(uint256 seed) internal pure returns (string memory) {
        uint256[9] memory values;

        values[0] = seed % 100 > 50 ? G : A;
        values[1] = seed % 500 > 195 ? G : D;
        values[2] = seed % 2000 > 1667 ? D : A;
        values[3] = (values[0] == G) && (values[0] == G) ? A : G;
        values[4] = (values[1] == A) ? T : G;
        values[5] = (values[1] == T) && seed % 2000 > 1667 ? G : A;
        values[6] = (values[0] == G) && (values[0] == A) ? A : G;
        values[7] = (values[2] == G) ? T : G;
        values[8] = (values[3] == G) ? A : G;

        string memory seq = string(
            abi.encodePacked(
                values[0],
                values[1],
                values[2],
                values[3],
                values[4],
                values[5],
                values[6],
                values[7],
                values[8]
            )
        );
        return seq;
    }

    function getSeed(uint256 tokenId) internal view returns (uint256) {
        uint256 seed = uint256(
            keccak256(
                abi.encode(
                    keccak256(
                        abi.encodePacked(
                            block.timestamp,
                            block.number,
                            tx.origin,
                            msg.sender,
                            gasleft(),
                            tokenId,
                            blockhash(block.number),
                            blockhash(block.number - 69)
                        )
                    )
                )
            )
        );
        return seed;
    }

    function roll(string memory input) internal pure returns (uint256) {
        return (random(input) % 6) + 1;
    }

    function random(string memory input) internal pure returns (uint256) {
        return uint256(keccak256(abi.encodePacked(input)));
    }

    function slot(
        string memory prefix,
        uint256 seed,
        uint256 mod
    ) internal pure returns (uint256) {
        return random(string(abi.encodePacked(prefix, seed))) % mod;
    }

    function getBloodSeed(uint256 tokenId, string memory imageHash)
        internal
        view
        returns (uint256)
    {
        uint256 seed = uint256(
            keccak256(
                abi.encode(
                    keccak256(abi.encodePacked(getSeed(tokenId), imageHash))
                )
            )
        );
        return seed;
    }
}

contracts/Shadowling.sol.content:
// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.6;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./libraries/metadata/ShadowlingMetadata.sol";
import "./libraries/Random.sol";
import "./libraries/MetadataUtils.sol";
import "./libraries/Currency.sol";

contract Shadowling is ShadowlingMetadata, Ownable, ReentrancyGuard {
    /// @notice Mints Shadowlings to `msg.sender`, cannot mint 0 tokenId
    /// @param  tokenId Token with `id` to mint. Maps id to individual item ids in ItemIds
    function claim(uint256 tokenId, address recipient)
        external
        nonReentrant
        onlyOwner
    {
        propertiesOf[tokenId] = Attributes.ids(tokenId);
        _safeMint(recipient, tokenId);
    }

    /// @notice Mints Shadowchain Origin Shadowlings to shadowpakt members, cannot mint 0 tokenId
    function summon(uint256 tokenId, address recipient)
        external
        nonReentrant
        onlyOwner
    {
        Attributes.ItemIds memory state = Attributes.ids(tokenId);
        state.origin = Attributes.originId(tokenId, true);
        propertiesOf[tokenId] = state;
        _safeMint(recipient, tokenId);
    }

    function modify(uint256 tokenId, uint256 currencyId)
        external
        nonReentrant
        onlyOwner
    {
        Attributes.ItemIds memory cache = propertiesOf[tokenId]; // cache the shadowling props

        string memory bloodline = Attributes.encodedIdToString(cache.bloodline);
        uint256 startSeed = Random.getBloodSeed(tokenId, bloodline);
        string memory sequence = Random.sequence(startSeed);
        uint256 seed = uint256(
            keccak256(
                abi.encodePacked("MODIFY", toString(currencyId), sequence)
            )
        );

        uint256[4] memory values;
        values[0] = cache.creature;
        values[1] = cache.flaw;
        values[2] = cache.ability;
        values[3] = cache.name;

        values = Currency.modify(currencyId, values, seed);

        cache.creature = values[0] > 0 ? Attributes.creatureId(values[0]) : 0;
        cache.flaw = values[1] > 0 ? Attributes.flawId(values[1]) : 0;
        cache.ability = values[2] > 0 ? Attributes.abilityId(values[2]) : 0;
        cache.name = values[3] > 0 ? Attributes.nameId(values[3]) : 0;

        propertiesOf[tokenId] = cache;
    }

    constructor(address altar) {
        transferOwnership(altar);
    }
}

contracts/libraries/metadata/ShadowlingM.content:
//SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.8.0;

import "./Attributes.sol";
import "./Stats.sol";
import "../TokenId.sol";
import { Base64, toString } from "../MetadataUtils.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";

/// @title Helper contract for generating ERC-1155 token ids and descriptions for
/// the individual items inside a Loot bag.
/// @author Georgios Konstantopoulos
/// @dev Inherit from this contract and use it to generate metadata for your tokens
contract ShadowlingMetadata is ERC721Enumerable {
    mapping(uint256 => Attributes.ItemIds) public propertiesOf;

    constructor() ERC721("Shadowling", "SHDW") {}

    /// @dev Opensea contract metadata: https://docs.opensea.io/docs/contract-level-metadata
    function contractURI() external pure returns (string memory) {
        string
            memory json = '{"name": "Shadowling", "description": "Shadowlings follow you in your journey across chainspace, the shadowchain, and beyond..."}';
        string memory encodedJson = Base64.encode(bytes(json));
        string memory output = string(
            abi.encodePacked("data:application/json;base64,", encodedJson)
        );

        return output;
    }

    /// @notice Returns an SVG for the provided token id
    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721)
        returns (string memory)
    {
        Attributes.ItemStrings memory props = properties(tokenId);
        string memory stats = Stats.render(tokenId);
        string memory json = Base64.encode(
            bytes(
                string(
                    abi.encodePacked(
                        '{ "name": "',
                        "Shadowling",
                        '", ',
                        '"description" : ',
                        '"Shadowlings follow you in your journey across chainspace, the shadowchain, and beyond...", ',
                        Attributes.render(props, stats),
                        Attributes.attributes(props)
                    )
                )
            )
        );
        string memory output = string(
            abi.encodePacked("data:application/json;base64,", json)
        );

        return output;
    }

    /// @notice Returns the attributes properties of a `tokenId`
    /// @dev Opensea Standards: https://docs.opensea.io/docs/metadata-standards
    function attributes(uint256 tokenId) public view returns (string memory) {
        return Attributes.attributes(properties(tokenId));
    }

    /// @notice Returns the attributes properties of a single item
    /// @dev Opensea Standards: https://docs.opensea.io/docs/metadata-standards
    /// @param  itemId A value in propertiesOf[tokenId]
    function attributesItem(uint256 itemId)
        public
        pure
        returns (string memory)
    {
        return Scanner.attributes(itemId);
    }

    function properties(uint256 tokenId)
        public
        view
        returns (Attributes.ItemStrings memory)
    {
        return Attributes.props(propertiesOf[tokenId]);
    }
}

