contracts/Void.sol.content:
// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.6;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Snapshot.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Votes.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract Void is ERC20Votes, Ownable, ReentrancyGuard {
    /// @notice Minter contract for Void tokens
    address public altar;

    function mint(address to, uint256 value) external onlyOwner {
        _mint(to, value);
    }

    function burn(address from, uint256 value) external onlyOwner {
        _burn(from, value);
    }

    constructor(address altar_)
        ERC20Permit("Void Token")
        ERC20("Void Token", "VOID")
    {
        altar = altar_;
        transferOwnership(altar_);
    }
}

contracts/Shadowpakt.sol.content:
// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IShadowpakt.sol";

/// @author Clement Lakhal
contract Shadowpakt is IShadowpakt, Ownable {
    /// STORAGE PROPERTIES ///

    /// @inheritdoc IShadowpakt
    mapping(address => bool) public override isWhitelisted;

    /// @inheritdoc IShadowpakt
    mapping(bytes32 => bool) public override isHashedKey;

    /// MODIFIERS ///

    /// @notice Restricts the call to a whitelisted sender
    modifier onlyWhitelisted() {
        require(isWhitelisted[msg.sender] == true, "Caller not whitelisted");

        _;
    }

    constructor() {}

    /// @inheritdoc IShadowpakt
    function addKeys(bytes32[] memory hashes) external override onlyOwner {
        for (uint256 i = 0; i < hashes.length; i += 1) {
            isHashedKey[hashes[i]] = true;
        }
    }

    /// @inheritdoc IShadowpakt
    function useKey(string memory key, address user) public virtual override {
        require(isWhitelisted[user] == false, "Already whitelisted");
        bytes32 hash = keccak256(abi.encodePacked(key));
        require(isHashedKey[hash] == true, "Invalid key");
        isWhitelisted[user] = true;
        isHashedKey[hash] = false;
        emit Whitelisted(user);
    }

    /// @inheritdoc IShadowpakt
    function blacklist(address user) external override onlyOwner {
        isWhitelisted[user] = false;
        emit Blacklisted(user);
    }

    /// VIEW FUNCTIONS ///

    /// @inheritdoc IShadowpakt
    function isKeyValid(string memory key)
        external
        view
        override
        returns (bool)
    {
        bytes32 hash = keccak256(abi.encodePacked(key));
        return isHashedKey[hash];
    }
}

contracts/interfaces/IShadowpakt.sol.content:
// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.0;

/// @author Clement Lakhal
interface IShadowpakt {
    /// EVENTS ///

    /// @notice Emitted when a user is whitelisted
    /// @param user The address of the user
    event Whitelisted(address user);

    /// @notice Emitted when a user is blacklisted
    /// @param user The address of the user
    event Blacklisted(address user);

    /// EFFECT FUNCTIONS ///

    /// @notice Adds new keys
    /// @param hashes An array of hashed keys to add
    function addKeys(bytes32[] memory hashes) external;

    /// @notice Uses a key
    /// @param key The key to use
    /// @param user The address of the user to whitelist
    function useKey(string memory key, address user) external;

    /// @notice Blacklists a user
    /// @param user The address of the user to blacklist
    function blacklist(address user) external;

    /// VIEW FUNCTIONS ///

    /// @notice Checks if a user is whitelisted
    /// @param user The address of the user to check
    /// @return True is the user is whitelisted
    function isWhitelisted(address user) external returns (bool);

    /// @notice Checks if a hashed key is valid
    /// @param hashedKey The hashed key to check
    /// @return True if the hashed key is valid
    function isHashedKey(bytes32 hashedKey) external returns (bool);

    /// @notice Checks if a key is valid
    /// @param key The key to inspect
    /// @return True if the key is valid
    function isKeyValid(string memory key) external returns (bool);
}

